mapper.select:
SELECT laundry_id AS laundryId, laundry_name AS laundryName, laundry_category AS laundryCategory, laundry_price AS laundryPrice, laundry_category_id AS laundryCategoryId FROM laundry
WHERE laundry_id = #{laundryId}
ORDER BY laundry_id

SELECT laundry_id AS laundryId, laundry_name AS laundryName, laundry_category AS laundryCategory, laundry_price AS laundryPrice, laundry_category_id AS laundryCategoryId FROM laundry
WHERE laundry_category_id = #{laundryCategoryId}
ORDER BY laundry_category_id
