mapper.select:
SELECT orders_id as ordersId, wash_id as washId, orders_count as ordersCount, orders_price as ordersPrice, orders_date as ordersDate, orders_status as OrdersStatus, member_id as memberId, laundry_id as laundryId FROM ORDERS
WHERE member_id = #{memberId}
ORDER BY orders_id

SELECT orders_id as ordersId, wash_id as washId, orders_count as ordersCount, orders_price as ordersPrice, orders_date as ordersDate, orders_status as OrdersStatus, member_id as memberId, laundry_id as laundryId FROM ORDERS
WHERE orders_id = #{ordersId}
ORDER BY wash_id

mapper.update:
UPDATE ORDERS
wash_id = #{washId}, orders_count = #{ordersCount}, orders_price = #{ordersPrice}, orders_date = #{ordersDate}, orders_comment = #{ordersComment}, orders_status = #{ordersStatus}, member_id = #{memberId}, laundry_id = #{laundryId}
WHERE orders_id = #{ordersId}
