mapper.select:
SELECT orders_id as ordersId, wash_id as washId, orders_count as ordersCount, orders_price as ordersPrice, ORDERS_COMMENT as ordersComment, orders_date as ordersDate, orders_status as OrdersStatus, member_id as memberId, laundry_id as laundryId, ORDERS_DIR_PATH as ordersDirPath FROM ORDERS WHERE member_id = #{memberId}
AND wash_id = #{washId}
ORDER BY orders_id DESC

SELECT orders_id as ordersId, wash_id as washId, orders_count as ordersCount, orders_price as ordersPrice, orders_date as ordersDate, ORDERS_COMMENT as ordersComment, orders_status as OrdersStatus, member_id as memberId, laundry_id as laundryId, orders_check_date as ordersCheckDate FROM ORDERS
WHERE wash_id = #{washId}
ORDER BY wash_id

SELECT count(orders_id)
FROM ORDERS

	WHERE member_id = #{memberId}

select count(orders_id)
from orders

	where trunc(orders_date) = trunc(sysdate);

mapper.update:
UPDATE ORDERS
wash_id = #{washId}, orders_count = #{ordersCount}, orders_price = #{ordersPrice}, orders_date = #{ordersDate}, orders_comment = #{ordersComment}, orders_status = #{ordersStatus}, orders_check_date = #{ordersCheckDate}, member_id = #{memberId}, laundry_id = #{laundryId}
WHERE orders_id = #{ordersId}

mapper.delete:
DELETE FROM ORDERS

	AND wash_id = #{washId} 
	AND orders_id = #{ordersId}

mapper.select:
SELECT * FROM( SELECT ROW_NUMBER() OVER(ORDER by MAX(orders_date) asc) AS rnum, wash_id as washId, COUNT(*) as ordersTotalCount, SUM(orders_price) as ordersTotalPrice, MAX(orders_date) as ordersDate, MAX(orders_status) as orderState FROM ORDERS WHERE member_id = #{memberId} GROUP BY wash_id )
WHERE rnum > #{start} AND rnum <= #{end}
ORDER BY ordersDate DESC

SELECT * FROM( SELECT ROW_NUMBER() OVER(ORDER by MAX(orders_date) asc) AS rnum, wash_id as washId, COUNT(*) as ordersTotalCount, SUM(orders_price) as ordersTotalPrice, MAX(orders_date) as ordersDate, MAX(orders_status) as orderState FROM ORDERS WHERE member_id = #{memberId} AND orders_date >= ADD_MONTHS(SYSDATE, -#{month}) GROUP BY wash_id )
WHERE rnum > #{start} AND rnum <= #{end}
ORDER BY ordersDate DESC
